from sequence_analysis import PairwiseAligner, Sequence

class TestDebug:
    def test_1(self):
        nuc = ""
        target = ""

        nuc_seq = Sequence(nuc)
        for orf in nuc_seq.get_open_reading_frames():
            prot = orf.protein_sequence
            pa = PairwiseAligner()
            pa.target = target
            pa.query = prot
            pa.align()
    
